mapper.insert:
insert into streamer(
	streamer_no, streamer_soop_id, streamer_name
)
values(
	#{streamerNo}, #{streamerSoopId}, #{streamerName}
)

mapper.select:
select * from (
	select rownum rn, TMP.* from(
		select * from streamer_stat_list
		order by official_ranking1 desc, official_ranking2 desc,
				official_ranking1 desc, streamer_name asc
		) TMP
) where rn between #{begin} and #{end}

select * from (
	select rownum rn, TMP.* from(
		select * from streamer_stat_list
		order by total_ranking1 desc, total_ranking2 desc,
			total_ranking1 desc, streamer_name asc
	) TMP
) where rn between #{begin} and #{end}

mapper.update:
update streamer 
set streamer_soop_id=#{streamerSoopId},
	streamer_name=#{streamerName}
where streamer_no = #{streamerNo}

mapper.select:
select * from (
	select rownum rn, TMP.* from(
		select * from streamer_stat_list
		where streamer_name LIKE '%' || #{keyword} || '%'
		order by official_ranking1 desc, official_ranking2 desc,
				official_ranking1 desc, streamer_name asc
	) TMP
) where rn between #{begin} and #{end}
