mapper.insert:
insert into streamer(
	streamer_no, streamer_soop_id, streamer_name
)
values(
	#{streamerNo}, #{streamerSoopId}, #{streamerName}
)

mapper.update:
update streamer 
set streamer_soop_id=#{streamerSoopId},
	streamer_name=#{streamerName},
	streamer_profile_url=#{streamerProfileUrl}
where streamer_no = #{streamerNo}
