mapper.insert:
insert into team(
	team_id, tournament_id, team_name,
	team_top, team_jug, team_mid, team_ad, team_sup,
	team_ranking, team_note
)
values(
	#{teamId}, #{tournamentId}, #{teamName}, 
	#{teamTop}, #{teamJug}, #{teamMid}, #{teamAd}, #{teamSup},     
	#{teamRanking}, #{teamNote}  
)

mapper.select:
select * from streamer_team_list
where team_top = #{streamerNo}
	or team_jug = #{streamerNo} or team_mid = #{streamerNo}
	or team_ad = #{streamerNo} or team_sup = #{streamerNo}
order by tournament_year desc, tournament_id desc
