mapper.insert:
insert into tournament(
	tournament_id, tournament_name, tournament_year,
	tournament_start, tournament_end,
	tournament_isofficial, tournament_tier_type
)
values(
	#{tournamentId},#{tournamentName},#{tournamentYear},
	#{tournamentStart},#{tournamentEnd},
	#{tournamentIsofficial},#{tournamentTierType}
)

mapper.select:
select * from (
	select rownum rn, TMP.* from(
		select * from tournament_list
		order by tournament_year desc, tournament_end desc
	) TMP
) where rn between #{begin} and #{end}

select * from tournament
where tournament_id = #{tournamentId}
order by tournament_year desc, tournament_end asc
